import React, { Component, Fragment } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';
import { getInitialData, saveQuestion, saveQuestionAnswer } from './utils/api';
import LoadingBar from 'react-redux-loading';
import Login from './components/login';
import NewQuestion from './components/newquestion';
import Navigation from './navigation';
import { receiveUsers, addQuestionToUser, answerQuestionToUser } from './users';
import { receiveQuestions, addQuestion, answerQuestion } from './questions';
import { showLoading, hideLoading } from 'react-redux-loading';

class App extends Component {
  componentDidMount() { 
   // this.props.dispatch(InitialData());
   return function InitialData () {
	return (dispatch) => {
    	dispatch(showLoading());
      	return getInitialData()
      		.then(({users, questions}) => {
        		dispatch(receiveUsers(users));
          		dispatch(receiveQuestions(questions));
          		dispatch(hideLoading());
        	})
    }
}
  }



  render() {
    return (
      <Router>
      	<Fragment>
      		<LoadingBar />
      		<div className='Navigation-container'>
      			<Navigation />
      		</div>
      		<hr className='hr-app-color' />
      		<div className='container'>
      			<div>
      				<Switch>
                      <Route path='/' exact component={Login} />
                      <Route path='/add' exact component={NewQuestion} />

					</Switch>
      			</div>
      		</div>
      	</Fragment>
      </Router>
    );
  }
}

export default connect()(App);
